    private void jBuildRadioButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBuildRadioButtonActionPerformed
        // If WindowsRB is selected, enabled the continue button.
        if(jBuildRadioButton.isSelected()) {
            jContinueFrameButton.setEnabled(true);            
        }
        
        if(!jBuildRadioButton.isSelected()) {
            jContinueFrameButton.setEnabled(false);            
        }
    }//GEN-LAST:event_jBuildRadioButtonActionPerformed

